package test

import (
	"encoding/hex"
	"fmt"
	gososerial "github.com/EmYiQing/Gososerial"
	"strings"
	"testing"
)

func TestCCK4(t *testing.T) {
	ser := gososerial.GetCCK4("calc.exe")
	serStr := strings.ToUpper(hex.EncodeToString(ser))
	stdStr := ""
	fmt.Println(serStr)
	fmt.Println(stdStr)
	fmt.Println(serStr == stdStr)
}
